
function encrypt(message){

    var result=""; 

    for (var i=0;i<message.length;i++){
        var letterCode=message.charCodeAt(i); 
        var binary=letterCode.toString(2);
         
        while(binary.length<8){
            binary= "0"+binary; 
        }
        result+=binary;
    }
    return result; 
}

function decrypt(message){
    var chunk; 
    var result= ""; 
    for(i=0; i< message.length; i+=8){
        chunk=message.substring(i, i+8); 
        var charCode= parseInt(chunk, 2); 
        var char= String.fromCharCode(charCode); 
        result+=char; 
    }
    return result; 
}
var bitString= ""; 

function xorMessage(bitString, message){
    var result=""; 
    for (i=0; i<message.length; i++){
        if(message[i]==bitString[i]){
            result+="0"; 
        }
        else{
            result+="1"; 
        }   
    }
    return result; 
}


